import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import AddNote from "./AddNote";

function App() {
  const [noteList, updateNoteList] = useState([]);

  // Fetch notes from backend on initial load
  useEffect(() => {
    fetch('http://localhost:5000/api/notetaker/GetNotes')
      .then(response => response.json())
      .then(data => updateNoteList(data))
      .catch(error => console.error('Error fetching notes:', error));
  }, []);

  // Add note to backend and update state
  function addNote(noteToAdd) {
    fetch('http://localhost:5000/api/notetaker/AddNotes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams(noteToAdd)
    })
    .then(response => response.json())
    .then(note => {
      updateNoteList(currentNotes => [...currentNotes, note]);
    })
    .catch(error => console.error('Error adding note:', error));
  }

  // Delete note from backend and update state
  function deleteNote(noteId) {
    fetch(`http://localhost:5000/api/notetaker/DeleteNotes?id=${noteId}`, {
      method: 'DELETE'
    })
    .then(response => response.json())
    .then(() => {
      updateNoteList(currentNotes => currentNotes.filter((note) => note.key !== noteId));
    })
    .catch(error => console.error('Error deleting note:', error));
  }

  return (
    <div>
      <Header />
      <AddNote onAdd={addNote} />
      {noteList.map((item, idx) => (
        <Note
          key={item.key}
          id={idx}
          title={item.title}
          content={item.content}
          onDelete={() => deleteNote(item.key)}
        />
      ))}
      <Footer />
    </div>
  );
}

export default App;
